{{
(() => {
  const prelimDeadline = new Date(data_table.selectedRow.prelim_deadline);
  const currentDate = new Date();

// If completed, show the completion date
if (Sel_Prelim_Result.selectedOptionValue != 0) {
  const completionDate = Inp_Prelim_Result_Date.selectedDate
    ? new Date(Inp_Prelim_Result_Date.selectedDate)
    : new Date(); // fallback to today's date

  const formattedDate = completionDate.toLocaleDateString('th-TH', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });

  return `เสร็จสิ้นเมื่อวันที่ ${formattedDate}`;
}


  // If not completed, calculate time remaining
  const timeDifference = prelimDeadline.getTime() - currentDate.getTime();
  const daysDifference = Math.ceil(timeDifference / (1000 * 60 * 60 * 24));

  if (daysDifference < 0) {
    const deadlineDate = prelimDeadline.toLocaleDateString('th-TH', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
    return `ครบกำหนดตั้งแต่วันที่ ${deadlineDate}`;
  } else {
    return `เหลือเวลาไต่สวนเบื้องต้นอีก ${daysDifference} วัน (${prelimDeadline.toLocaleDateString('th-TH')})`;
  }
})()
}}
