{{ 
  (() => {
    const thaiMonths = {
      'Jan': 'ม.ค.',
      'Feb': 'ก.พ.',
      'Mar': 'มี.ค.',
      'Apr': 'เม.ย.',
      'May': 'พ.ค.',
      'Jun': 'มิ.ย.',
      'Jul': 'ก.ค.',
      'Aug': 'ส.ค.',
      'Sep': 'ก.ย.',
      'Oct': 'ต.ค.',
      'Nov': 'พ.ย.',
      'Dec': 'ธ.ค.'
    };

    const date = new Date(data_table.selectedRow.inves_deadline);
    const day = date.getDate().toString().padStart(2, '0');
    const monthShort = date.toLocaleDateString('en-UK', { month: 'short' });
    const monthThai = thaiMonths[monthShort] || monthShort;
    const yearBE2Digit = (date.getFullYear() + 543).toString().slice(-2);

    return `${day} ${monthThai} ${yearBE2Digit}`;
  })()
}}
