backgroundColor:
{{
(() => {
  // Retrieve the selected date from the Inp_Order_Num_Date widget
  const selectedDate = new Date(data_table.selectedRow.inves_deadline);

  // Validate the selected date
  if (!selectedDate) {
    return 'white'; // Default to white background if no date is selected
  }

  const invesDeadline = new Date(selectedDate);
  const currentTime = new Date().getTime();

  // Check if the date is valid
  if (isNaN(invesDeadline.getTime())) {
    return 'white'; // Default to white background if the date is invalid
  }

  // Check if the investigation status is 'มีมติแล้ว' (status code 7)
  if (Sel_Inves_Result.selectedOptionValue != 0) {
    return 'green'; // Green background for completed status
  }

  // Determine if the current date is past the investigation deadline
  if (currentTime > invesDeadline.getTime()) {
    return 'red'; // Red background for overdue
  } else {
    return 'white'; // White background for ongoing
  }
})()
}}

text:
{{
(() => {
  // Retrieve the investigation status from the selected row or the selected option
  const invesStatus = data_table.selectedRow.inves_status || Sel_Inves_Status.selectedOptionValue;

  // Check if the investigation is completed
  if (Sel_Inves_Result.selectedOptionValue != 0) {
    return 'เสร็จสิ้นกระบวนการไต่สวนชี้มูล';
  }

  // Parse the investigation deadline date from the selected row
  const invesDeadline = new Date(data_table.selectedRow.inves_deadline);
  const currentDate = new Date();

  // Calculate the time difference between the current date and the investigation deadline
  const timeDifference = invesDeadline.getTime() - currentDate.getTime();
  const daysDifference = Math.ceil(timeDifference / (1000 * 60 * 60 * 24));

  // Determine if the case is overdue or has time remaining
  if (daysDifference < 0) {
    const daysOverdue = Math.abs(daysDifference);
    return `เลยกำหนดมาแล้ว ${daysOverdue} วัน`;
  } else {
    return 'ปกติ';
  }
})()
}}

