{{(() => {
  // Retrieve the investigation status from the selected row or the selected option
  const invesStatus = data_table.selectedRow.inves_status || Sel_Inves_Status.selectedOptionValue;

// If completed, show the completion date
if (Sel_Inves_Result.selectedOptionValue != 0) {
  const completionDate = Inp_Inves_Result_Date.selectedDate
    ? new Date(Inp_Inves_Result_Date.selectedDate)
    : new Date(); // fallback to today's date

  const formattedDate = completionDate.toLocaleDateString('th-TH', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });

  return `เสร็จสิ้นเมื่อวันที่ ${formattedDate}`;
}
  // Parse the investigation deadline date from the selected row
  const invesDeadline = new Date(data_table.selectedRow.inves_deadline);
  const currentDate = new Date();

  // Calculate the time difference between the current date and the investigation deadline
  const timeDifference = invesDeadline.getTime() - currentDate.getTime();
  const daysDifference = Math.ceil(timeDifference / (1000 * 60 * 60 * 24));

  // Format the investigation deadline date
  const formattedInvesDeadline = invesDeadline.toLocaleDateString('th-TH', {
    year: 'numeric',
    month: 'long',
    day: 'numeric',
  });

  // Determine if the case is overdue or has time remaining
  if (daysDifference < 0) {
    // Calculate the number of days overdue
    const daysOverdue = Math.abs(daysDifference);
    return `เลยกำหนดมาแล้ว ${daysOverdue} วัน 
(ครบกำหนดวันที่ ${formattedInvesDeadline})`;
  } else {
    return `เหลือเวลาอีก ${daysDifference} วัน 
(ครบกำหนดวันที่ ${formattedInvesDeadline})`;
  }
})();
}}
