(() => {
  // Retrieve the case number date from the selected row
  const caseNumberDate = new Date(data_table.selectedRow.received_date);

  // Check if the date is valid
  if (isNaN(caseNumberDate.getTime())) {
    return 'วันที่รับคดีไม่ถูกต้อง'; // Return an error message if the date is invalid
  }

  // Define options for formatting the date
  const options = {
    day: '2-digit',
    month: 'short',
    year: '2-digit'
  };

  // Format the date to Thai locale with short month names
  const formattedDate = new Intl.DateTimeFormat('th-TH', options).format(caseNumberDate);

  // Construct the final string
  const result = `รับสำนวนคดี ${formattedDate}`;

  // Return the result for display in the text widget
  return result;
})()
}}
