{{ 
  (() => {
    const thaiMonths = [
      'ม.ค.',   // January (0)
      'ก.พ.',   // February (1)
      'มี.ค.',  // March (2)
      'เม.ย.',  // April (3)
      'พ.ค.',   // May (4)
      'มิ.ย.',  // June (5)
      'ก.ค.',   // July (6)
      'ส.ค.',   // August (7)
      'ก.ย.',   // September (8)
      'ต.ค.',   // October (9)
      'พ.ย.',   // November (10)
      'ธ.ค.'    // December (11)
    ];

    const date = new Date(data_table.selectedRow.prelim_deadline);
    const day = date.getDate().toString().padStart(2, '0');
    const monthThai = thaiMonths[date.getMonth()]; // getMonth() returns 0-11
    const yearBE2Digit = (date.getFullYear() + 543).toString().slice(-2);

    return `${day} ${monthThai} ${yearBE2Digit}`;
  })()
}}
